        public static Level GetLevel(LevelDifficulties difficulty, int x)
        {
            switch (difficulty)
            {
                case LevelDifficulties.Easy:
                    return GetEasy(x);
                    
                case LevelDifficulties.Medium:
                    return GetMedium(x);
                    
